import React from "react";
import { useHistory } from "react-router";
import GoogleLogin from "react-google-login";

// CSS imports
import sign from "./sign.module.scss";
import { getGoogleClientID } from "../envData/index";

export const Sign = () => {
  const history = useHistory();

  const responseGoogle = (response) => {
    if (response.profileObj) {
      const user = {
        email: response.profileObj.email,
        name: response.profileObj.name,
        image: response.profileObj.imageUrl,
      };
      if (
        user.email.includes("@revvsales.com") ||
        user.email.includes("@austinmonks.com")
      ) {
        history.push({
          pathname: "/apps",
          state: { user },
        });
      } else {
        alert("No Entry");
      }
    }
  };

  return (
    <div className={sign.background}>
      <div className={sign.signDiv}>
        <h3>Welcome to</h3>
        <img
          src="https://d34fwl795uvawg.cloudfront.net/images/revv-logo-color.svg"
          alt="logo"
        />
        <div className={sign.one}>ONE</div>
        <p>Your one stop place for all the internal tools</p>

        <GoogleLogin
          clientId={getGoogleClientID()}
          buttonText="Sign in with RevvSales email"
          style={{ color: "black" }}
          theme={"dark"}
          className={sign.googleBtn}
          onSuccess={responseGoogle}
          onFailure={responseGoogle}
        />
      </div>
      {/* <div className={sign.disclaimer}>
        This is a Revv internal tool. Reach out to <b>Araa</b> or{" "}
        <b>Soumitha</b> for the access.
      </div> */}
    </div>
  );
};
