import React, { useEffect, useState } from "react";
import { Redirect, useHistory, useLocation } from "react-router";
import { Link } from "react-router-dom";
import navbar from "./navBar.module.scss";
import MENU from "../../assets/ic_menu.svg";

export const NavBar = () => {
  const [name, setName] = useState("");
  const location = useLocation();
  const history = useHistory();

  useEffect(() => {
    setName(location.pathname.slice(1, location.pathname.length));
  }, [location]);

  const handleBack = () => {
    history.push({
      pathname: "/apps",
      state: location.state,
    });
  };
  console.log(location.state === "undefined");
  if (location.state) {
    return <Redirect to="/signin" />;
  } else {
    return (
      <div className={navbar.navbar}>
        <div className={navbar.navbarLogo}>
          <img
            onClick={handleBack}
            src="https://d34fwl795uvawg.cloudfront.net/images/revv-logo-color.svg"
            alt="logo"
          />
          <span>{name}</span>
        </div>
        <div className={navbar.rightPane}>
          {name !== "apps" && (
            <div className={navbar.menuContainer} onClick={handleBack}>
              <img src={MENU} alt="Menu" className={navbar.menu} />
            </div>
          )}

          <div className={navbar.profile}>
            <img src={location.state.user["image"]} alt="user-profile" />
            <div className={navbar.dropdown}>
              <Link to="/signin">logout</Link>
            </div>
          </div>
        </div>
      </div>
    );
  }
};
