import React from "react";
import { Redirect, useHistory, useLocation } from "react-router";
import apps from "./apps.module.scss";
import GREYTHR from "../../assets/logo_greythr.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export const Apps = () => {
  const history = useHistory();
  const location = useLocation();

  const users = [
    "nishant@revvsales.com",
    "nishant@austinmonks.com",
    "vidit@austinmonks.com",
    "vidit@revvsales.com",
    "araa@revvsales.com",
    "araa@austinmonks.com",
    "soumitha@revvsales.com",
    "aswathy.a@revvsales.com",
    "amit.k@revvsales.com",
    "aswathy.a@austinmonks.com",
    "amit.k@austinmonks.com",
    "anand@revvsales.com",
  ];

  const handleChangeUrl = (value) => {
    if (value === "metrics") {
      if (users.find((item) => item === location.state["user"].email)) {
        history.push({
          pathname: `/${value}`,
          state: location.state,
        });
      } else {
      }
    } else {
      history.push({
        pathname: `/${value}`,
        state: location.state,
      });
    }
  };

  if (!location.state) {
    return <Redirect to="/signin" />;
  } else {
    return (
      <div className={apps.background}>
        <div className={apps.appContainer}>
          <div className={apps.appssection}>
            <div className={apps.sectionTitle}>Public</div>
            <div className={apps.apps}>
              <a
                className={apps.appItem}
                href="https://austinmonks-hr.revv.so/"
                target="blank"
              >
                <div className={apps.applogo}>
                  <FontAwesomeIcon icon={["fad", "calendar-day"]} />
                </div>
                <div className={apps.appTitle}>HR Portal</div>
                <div className={apps.appDesc}>
                  Request for leave, Travel & laptops{" "}
                </div>
              </a>
              <a
                href="https://austinmonks.greythr.com/"
                target="blank"
                className={apps.appItem}
              >
                <div className={apps.applogo}>
                  <img src={GREYTHR} alt="GretHR logo"></img>
                </div>
                <div className={apps.appTitle}>Payroll</div>
                <div className={apps.appDesc}>
                  Manage Pay slips and other payroll documents
                </div>
              </a>
              <a
                className={apps.appItem}
                href="https://austinmonks.greythr.com/"
                target="blank"
              >
                <div className={apps.applogo}>
                  <FontAwesomeIcon icon={["fad", "paint-roller"]} />
                </div>
                <div className={apps.appTitle}>Design System</div>
                <div className={apps.appDesc}>
                  Download Revv logo and other assets
                </div>
              </a>
            </div>
          </div>
          <div className={apps.appssection}>
            <div className={apps.sectionTitle}>Limited access</div>
            <div className={apps.apps}>
              <div
                className={apps.appItem}
                onClick={() => handleChangeUrl("metrics")}
                role="button"
              >
                <div className={apps.applogo}>
                  <FontAwesomeIcon icon={["fad", "tachometer-alt-fast"]} />
                </div>
                <div className={apps.appTitle}>Metrics</div>
                <div className={apps.appDesc}>
                  Manage soft limits and customer data
                </div>
              </div>
              <div
                className={apps.appItem}
                onClick={() => handleChangeUrl("show")}
                role="button"
              >
                <div className={apps.applogo}>
                  <FontAwesomeIcon icon={["fad", "camera-movie"]} />
                </div>
                <div className={apps.appTitle}>Show</div>
                <div className={apps.appDesc}>
                  The native public api demo for Sales
                </div>
              </div>
              <div className={apps.appItem} role="button">
                <div className={apps.applogo}>
                  <FontAwesomeIcon icon={["fad", "store"]} />
                </div>
                <div className={apps.appTitle}>EComm Admin</div>
                <div className={apps.appDesc}>
                  Manage and generate new templates
                </div>
              </div>
            </div>
            <div className={apps.sectionDesc}>
              <i>
                <FontAwesomeIcon icon={["fad", "info-circle"]} />
              </i>{" "}
              These are our internal tools. Reach out to Araa or Soumitha for
              the access
            </div>
          </div>
        </div>
      </div>
    );
  }
};
